// jquery
jQuery(function($) {

	// get the page id
	var bodyclass = $('body').prop('class').split(' ');
	$.each(bodyclass, function( index, value ) {
		if( value.substring(0, 8) == "page-id-" ) {
			pageid = value.replace( "page-id-", "" );
		}
	});

	// media element test
	$('.video-links a').on('click', function(e) {

		var loadVideoURL = $(this).data('url');
		var playerId = $(this).parent().parent().prop('id').replace('video-thumbs-', '');

		// console.log('loadVideoURL', loadVideoURL);
		// console.log('pageid', pageid);
		// console.log('playerId', playerId);

		var player = $('#video-' + pageid + '-' + playerId)[0].player.media;
		player.setSrc(loadVideoURL);
		player.play();

		e.preventDefault();
	});

	// set a variable with the window width
	var windowWidth = $(window).width();

	// function to wait for the final event
	var waitForFinalEvent = (function () {
		var timers = {};
		return function (callback, ms, uniqueId) {
			if (!uniqueId) {
				uniqueId = "Don't call this twice without a uniqueId";
			}
			if (timers[uniqueId]) {
				clearTimeout (timers[uniqueId]);
			}
			timers[uniqueId] = setTimeout(callback, ms);
		};
	})();

	// initial functionality based on window width
	if(windowWidth < 768) {

		// close navbar when clicking a menu item
		$('.navbar-collapse a:not(.dropdown-toggle)').on('click', function(){
			$(".navbar-collapse").collapse('hide');
		});

		// close navbar when clicking outside
		$(document).on('click',function(e) {
			var opened = $(".navbar-collapse").hasClass("navbar-collapse collapse in");
			if( opened && !$(e.target).hasClass('form-control') ) {
				$('.collapse').collapse('hide');
			}
		});

		$('.pagination').removeClass('pagination-sm').addClass('pagination-lg');

	} else {

		$('.pagination').removeClass('pagination-lg').addClass('pagination-sm');

	}

	// reinitialize functionality on window resize
	$(window).resize(function () {
		waitForFinalEvent(function(){

			var windowWidth = $(window).width();

			if(windowWidth < 768) {

				// close navbar when clicking a menu item
				$('.navbar-collapse a:not(.dropdown-toggle)').on('click', function(){
					$(".navbar-collapse").collapse('hide');
				});

				// close navbar when clicking outside
				$(document).on('click',function(e) {
					var opened = $(".navbar-collapse").hasClass("navbar-collapse collapse in");
					if( opened && !$(e.target).hasClass('form-control') ) {
						$('.collapse').collapse('hide');
					}
				});

				$('.pagination').removeClass('pagination-sm').addClass('pagination-lg');

			} else {

				$('.pagination').removeClass('pagination-lg').addClass('pagination-sm');

			}

		}, 500, "window_resize_event");
	});

	// clicking on active page in pagination
	$('.pagination .active > a').on('click', function(e) {
		e.preventDefault();
	});

	// colorbox lightbox plugin
	$('.wp-caption a, a.lightbox').colorbox({
		rel:'lightbox',
		width:"90%",
		height:"90%"
	});
	$('.gallery a').colorbox({
		rel:'gal',
		width:"90%",
		height:"90%"
	});

	// touch functions for bootstrap carousel
	$('.carousel').swiperight(function() {
		$(this).carousel('prev');
	});
	$('.carousel').swipeleft(function() {
		$(this).carousel('next');
	});

	// keyboard functions for bootstrap carousel
	$(document).bind('keyup', function(e) {
		if(e.which == 39) {
			$('.carousel').carousel('next');
		} else if(e.which == 37) {
			$('.carousel').carousel('prev');
		}
	});

	// equal heights for footer widgets
	$('.footer-sidebar .well').matchHeight(true);

	// equal heights for the tab panes
	var maxHeight = -1;
	$('.tab-pane').each(function() {
		maxHeight = maxHeight > $(this).height() ? maxHeight : $(this).height();
	});
	$('.tab-pane').each(function() {
		$(this).height(maxHeight);
	});

	// highlight code on click
	$('.copytext').click(function() {
		if ( $('#tmp').length ) {
			$('#tmp').remove();
		}
		var clickText = $(this).text();
		$('<textarea id="tmp" class="copytextarea" />')
			.appendTo($(this))
			.val(clickText)
			.focus()
			.select();
		return false;
	});

	// remove highlight on focusout
	$('.copytext').focusout(function() {
		$('#tmp').remove();
	});

	// do not scroll to the top of the page when submitting # forms
	$('form').on('submit', function(e) {
		if( $(this).attr('action') == '#' ) {
			e.preventDefault();
		}
	});

/* ******************************************************************** */
/*                      USER CUSTOMIZATIONS                             */
/* ******************************************************************** */
// 

	// $('.your-class').slick({
	//   setting-name: setting-value
	// });



	$('.hp-slider').slick({
		dots: true,
		infinite: true,
		speed: 300,
	  	slidesToShow: 1,
	  	arrows: true,
	  	fade: true,
	  	cssEase: 'linear',
	  	slidesToScroll: 1,
	  	autoplay: true,
	  	autoplaySpeed: 3000
	  	
	});

	$("[href]").each(function() {
	     if (this.href == window.location.href) {
	         $(this).addClass("active");
	     }
	 });


	// do not scroll to top of page when clicking # hrefs
	// $('a').on('click', function(e) {
	// 	if( $(this).prop('href') == '#' ) {
	// 		e.preventDefault();
	// 	}
	// });

	// open external links in a new window
	var h = window.location.host.toLowerCase();
	$("a:not(a:has(img))[href^='http']:not([href^='http://" + h + "']):not([href^='http://www." + h + "']):not([href^='https://" + h + "']):not([href^='https://www." + h + "']):not([href^='mailto']), a[href$='.pdf']").attr("target", "_blank");

	// open rss feed links in a new window
	$("a[href*='/feed/']").attr("target", "_blank");

	// remove right arrow quote from author link


}); // end jquery